import React, { Component, useEffect, useState} from 'react';
import {Container, Row, Col, Form, Button, Card} from 'react-bootstrap';

export default function Blog(props) {

  const formatTime = (time) => {
    const timestamp = new Date(time);
    var formattedTimestamp = Intl.DateTimeFormat('en-US',{
      dateStyle: "short",
      year: "numeric",
      month: "short",
      day: "2-digit",
      hour: "numeric",
      minute: "2-digit",
      second: "2-digit"
    }).format(timestamp);
    return formattedTimestamp;
  }

  return(
    <div>
      <Container fluid>
        <Row>
          <Col>
            {Object.keys(props.data).map((id) => (
              <Card>
                <Card.Header>{
                  formatTime(props.data[id].time)
                }</Card.Header>
                <Card.Body>
                <Card.Title><h5>{props.data[id].name}</h5></Card.Title>
                <Card.Subtitle className="mb-2 text-muted">{props.data[id].description}</Card.Subtitle>
                  <Card.Text>
                    {props.data[id].message}
                  </Card.Text>
                </Card.Body>
              </Card>
            ))}

          </Col>
        </Row>
      </Container>
    </div>
  )

}
