  constructor(){
    super();
    this.state= {
      activeTab: 1
    }
  }

  render() {
    const tabs = [
      {id: 1, title: "Home"},
      {id: 2, title: "Images"},
      {id: 3, title: "Media"},
      {id: 4, title: "Projects"}
    ]

    let page;
    if (this.state.activeTab == 1){
      page = <Home />;
    }
    return (
      <div>
        <div>
          <TabList tabs={tabs}/>
        </div>

        <div>
          {page}
        </div>
