        </BrowserRouter> */}
        <StickyFooter
          className = "pl-4 pt-4"
        >
            <p> {'\u00A9'} Copyright 2020, Bik Nandy</p>
        </StickyFooter>
      </div>
    )
  }
}

export default App;
