import React, { Component, useEffect, useState} from 'react';
import {Container, Row, Col, Form, Button, Card} from 'react-bootstrap';
import movies from "../movies.json";
import axios from 'axios';

export default function Movies() {

  const [movieData, setMovieData] = useState([])
  var promises;
  //ed114b73 - API KEY

  //retrieve all information from axios
  //map through each element and retrieve posters

  useEffect(() => {
    Object.values(movies).forEach((movieID) => {
      url = "http://www.omdbapi.com/?apikey=ed114b73&i="+movieID;
      movieData.push (axios.get(url));
    });

    axios.all(promises).then((results) => {
      results.forEach((res) => {
          setMovieData(movieData => [...movieData, res.data])
          console.log(movieData)
      })
    });

  })


  return(
    <div>
      <Container fluid>
        <Row>
          <Col>
            <h1 className = "ml-3 mt-3"> My Favorite Movies </h1>
          </Col>
        </Row>
        <Row>

        </Row>
      </Container>
    </div>
  )

}
